package com.example.aplicacioncurso;

import androidx.appcompat.app.AppCompatActivity;

import android.os.Bundle;
import android.widget.ImageView;

import com.bumptech.glide.Glide;

/*
Autor: Juan Francisco Sánchez González
Fecha: 29/12/2023
Clase: Actividad inicial de login que utiliza la biblioteca Glide para mostrar una imagen de una url.
*/

public class MainActivity extends AppCompatActivity {

    private ImageView imagenLogo;

    // url de imagen a mostrar
    final String URL_LOGO = "data:image/png;base64,";

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        // Componente Imagen Logo (Glide)
        imagenLogo = (ImageView) findViewById(R.id.imageViewLogo);
        Glide.with(this)
                .load(URL_LOGO)
                .into(imagenLogo);
    }
}